	listEasyIEPRunHistories <- function(searchConditionsList = NULL, EasyIEPRunHistoryID = F, SchoolYearID = F, DistrictID = F, StartTime = F, EndTime = F, ProcessType = F, ImportType = F, IsLocked = F, ExceptionCount = F, AddCount = F, UpdateCount = F, DeleteCount = F, Status = F, RunData = F, UserIDCanceledBy = F, UserIDCreatedBy = F, CreatedTime = F, UserIDModifiedBy = F, ModifiedTime = F, RunParameters = F, fieldPaths = NULL, searchConditionsGroupType = "And", searchSortFieldNamesList = NULL, searchSortFieldNamesDescendingList = NULL, entityId = 1, schoolYearId = NULL, page = 1, pageSize = 100000, flatten = T, returnResponse = F){

		params <- as.list(environment())

		searchFields <- params %>% purrr::keep(names(params) %>% stringr::str_sub(1,1) == names(params) %>% stringr::str_sub(1,1) %>% stringr::str_to_upper())

